import React,{Component} from 'react';

import ReactDOM from 'react-dom'
import { Nav,Navbar,NavDropdown,Form,FormControl,Button } from 'react-bootstrap';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Link from '@material-ui/core/Link';
import MaterialTable from 'material-table'

import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}


const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  homecoverdiv: {
    display:'flex',
    alignItems:'center',
    justifyContent:'center',
    width:'100%',
  },
  homecover: {
    width:'100%',
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
    backgroundColor: '#f7ebea',
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
  iconColor: {
    backgroundColor:'#9e7d7a',
  }
}));

const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];


export default function ProductForSeller() {
  const classes = useStyles();

  const addToCart = (product) => {
    const cart = this.state.cart;
    cart.push(product);
 
    this.setState({
        cart
    });
}
  



  return (
    <React.Fragment>
      
      <Navbar className="brand-bar" style={{justifyContent:'space-between'}}>
        <Navbar.Brand>Stella Boutique</Navbar.Brand>
          <div>
            <Button href="" className={classes.navButtons} variant="contained" color="primary">
              logout
            </Button>
          </div>
      </Navbar>
      
      <MaterialTable
        editable={{
            isEditable: rowData => rowData.name === 'a', // only name(a) rows would be editable
            isEditHidden: rowData => rowData.name === 'x',
            isDeletable: rowData => rowData.name === 'b', // only name(b) rows would be deletable,
            isDeleteHidden: rowData => rowData.name === 'y',
            onBulkUpdate: changes => 
                new Promise((resolve, reject) => {
                    setTimeout(() => {
                        /* setData([...data, newData]); */

                        resolve();
                    }, 1000);
                }),
            onRowAddCancelled: rowData => console.log('Row adding cancelled'),
            onRowUpdateCancelled: rowData => console.log('Row editing cancelled'),
            onRowAdd: newData =>
                new Promise((resolve, reject) => {
                    setTimeout(() => {
                        /* setData([...data, newData]); */

                        resolve();
                    }, 1000);
                }),
            onRowUpdate: (newData, oldData) =>
                new Promise((resolve, reject) => {
                    setTimeout(() => {
                        const dataUpdate = [...data];
                        const index = oldData.tableData.id;
                        dataUpdate[index] = newData;
                        setData([...dataUpdate]);

                        resolve();
                    }, 1000);
                }),
            onRowDelete: oldData =>
                new Promise((resolve, reject) => {
                    setTimeout(() => {
                        const dataDelete = [...data];
                        const index = oldData.tableData.id;
                        dataDelete.splice(index, 1);
                        setData([...dataDelete]);

                        resolve();
                    }, 1000);
                })
        }}
      />
    </React.Fragment>
  );
}
